import React from 'react'
import "./item-to-replace.style.scss";
import Korn from "../../assets/korn.png";

const item = {
    Qty: 1,
    contentQty: "450 גרם",
    price: "19.90"
}

const ItemToReplace = ({}) => {
    return ( 
        <div className="item-frame">
            <strong><p>ברנפלקס של אלופים עם קראנצ'ים</p></strong>
            <div className="item-frame__box">
                <img className="item-frame__image" src={Korn} alt="brenflex" />
                <div className="item-frame__details">
                    <span className="item-frame__sticker">אין במלאי!</span>
                    <span className="item-frame__qty"><strong>כמות:</strong></span>
                    <span><strong>&#8362;{`${item.price}`}</strong>{` / ${item.contentQty}`}</span>
                    <strong><p>{`סה"כ:`} {`${item.price * item.Qty}`}</p></strong>
                    <button className="item-frame__more-items btn">פריטים דומים</button>
                </div>
            </div>
        </div>
     );
}
 
export default ItemToReplace;
