import React from 'react';

import Korn from "../../assets/korn.png";
import "./chosen-item.style.scss";

const ChosenItem = ({itemImg, altItemImg}) => {

    return ( 
        <div className="chosen-item-frame">
            <div className="chosen-item-frame__box">
                <img className="chosen-item-frame__image" src={itemImg} alt="brenflex"/>
                <span style={{fontSize: "2rem"}}>&#8592;</span>
                <img className="chosen-item-frame__image" src={altItemImg} alt="brenflex"/>
            </div>
            <button className="item-frame__more-items btn">פריטים חליפיים &#8592;</button>
        </div>
     );
}
 
export default ChosenItem;
