import React, { Component } from 'react';
import ItemToReplace from "../item-to-replace/item-to-replace.component";
import "./item-to-replace-list.style.scss";

class ItemToReplaceList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            showAdditionalItems: false
        };
    }

    onShowAdditionalItemsClick = (additionalItems = null) => {
      
        this.setState(state => {
            showAdditionalItems: !state.showAdditionalItems
        });
    }

    render() { 
        const {items} = this.props;
        const itemsArr = items.map(item => {
            return (
                <ItemToReplace item={item} onShowAdditionalItemsClick={this.onShowAdditionalItemsClick}/>
            );
        });
        return ( 
            <div className="content">
                {itemsArr}
            </div>
         );
    }
}
 
export default ItemToReplaceList;
