import React, { Component } from 'react';
import ItemToReplace from "../item-to-replace/item-to-replace.component";
import AdditionalItemList from "../additional-item-list/additional-item-list.component";
import "./item-to-replace-list.style.scss";

class ItemToReplaceList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            showAdditionalItems: false,
            additionalItems: []
        };
    }

    onShowAdditionalItemsClick = (additionalItems = null) => {
        this.setState(state => ({
            showAdditionalItems: !state.showAdditionalItems,
            additionalItems,
        }));
    }

    render() { 
        const {items} = this.props;
        const {showAdditionalItems, additionalItems} = this.state;
        let component;

        if(showAdditionalItems) {
            component = <AdditionalItemList additionalItems={additionalItems}
            onShowAdditionalItemsClick={this.onShowAdditionalItemsClick} />
        } 
        else {
            component = items.map(item => {
                return (
                    <ItemToReplace key={item.Code} 
                    item={item}
                    onShowAdditionalItemsClick={this.onShowAdditionalItemsClick}/>
                );
            });
        }
        return ( 
            <div className="content">
                {component}
            </div>
         );
    }
}
 
export default ItemToReplaceList;
