import React, { Component } from "react";
import Header from "./components/header/header.component";
import ItemToReplaceList from "./components/item-to-replace-list/item-to-replace-list.component";
import Footer from "./components/footer/footer.component";
import itemsObject from "./assets/jsonFormat";
import AdditionalItemList from "./components/additional-item-list/additional-item-list.component";
import { Router, Route, Switch } from "react-router-dom";

import "./App.scss";

class App extends Component {
  constructor() {
    super();
    this.state = {
      dataObj: {},
      showAdditionalItems: false,
      additionalItems: [],
      currentItemCode: "",
      chosenItems: new Map(),
    };
  }

  onShowAdditionalItemsClick = (
    additionalItems = null,
    itemCode = null,
    closeWindow = false
  ) => {
    let { chosenItems, currentItemCode } = this.state;

    if (itemCode !== currentItemCode && itemCode !== null) {
      this.setState({ currentItemCode: itemCode });
    }
    if (additionalItems != null && itemCode != null) {
      chosenItems[itemCode] = additionalItems;
    }
    if (closeWindow) {
    }
    this.setState((state) => ({
      showAdditionalItems: !state.showAdditionalItems,
      additionalItems,
      chosenItems,
    }));
  };

  onAdditionalItemClick = (chosenItemCode, itemToReplaceCode) => {
    const chosenItem = {
      chosenItemCode,
      itemToReplaceCode,
    };
  };

  componentDidMount() {
    this.setState({ dataObj: itemsObject });
  }

  render() {
    const { dataObj, showAdditionalItems, additionalItems } = this.state;
    const isDataReceivedFromServer = Object.keys(dataObj).length > 0;
    let returnedComponent, subComponent;
    console.log(this.state.currentItemCode);
    if (isDataReceivedFromServer) {
      if (showAdditionalItems) {
        subComponent = (
          <AdditionalItemList
            additionalItems={additionalItems}
            onShowAdditionalItemsClick={this.onShowAdditionalItemsClick}
            onAdditionalItemClick={this.onAdditionalItemClick}
          />
        );
      } else {
        subComponent = (
          <ItemToReplaceList
            items={dataObj.Items}
            onShowAdditionalItemsClick={this.onShowAdditionalItemsClick}
          />
        );
      }

      returnedComponent = (
        <React.Fragment>
          <Header
            lastName={dataObj.LastName}
            firstName={dataObj.FirstName}
            msg={dataObj.Message}
          />
          {subComponent}
          <Footer />
        </React.Fragment>
      );
    } else {
      returnedComponent = <span>loading..</span>;
    }
    return <div className="container">{returnedComponent}</div>;
  }
}

export default App;
