import React from 'react'
import "./item-to-replace.style.scss";
import Korn from "../../assets/korn.png";

const item = {
    Qty: 1,
    contentQty: "450 גרם",
    price: "19.90"
}

const ItemToReplace = ({}) => {
    return ( 
        <div className="item-frame">
            <p>ברנפלקס של אלופים עם קראנצ'ים</p>
            <div className="item-frame__box">
                <img className="item-frame__image" src={Korn} alt="brenflex" />
                <div className="item-frame__details">
                    <p>{`${item.price} / ${item.contentQty}`}</p>
                    <strong><p>{`סה"כ: ${item.price * item.Qty}`}</p></strong>
                </div>
            </div>
        </div>
     );
}
 
export default ItemToReplace;
