import React from 'react'
import "./additional-item.style.scss";
import pic from "../../assets/korn.png";

const item = {
    Name: "ברנפלקס קאנצ'ים עם תפוזים",
    SalePrice: 4.50,
    Qty: 2,
    ContentQty: "100 ג'"
}

const AdditionalItem = ({}) => {
    return ( 
        <div className="additional-item-frame">
            <button className="additional-item-frame__close-btn btn"></button>
            <div className="additional-item-frame__box">
                 <strong><p className="item-frame__topic">{item.Name}</p></strong>
                <img className="additional-item-frame__image" src={pic} alt="additional-item" />
                <div className="additional-item-frame__details">
                    <span><strong>&#8362;{`${item.SalePrice}`}</strong>{` / ${item.ContentQty}`}</span>
                    <strong><p>{`סה"כ:`} {`${item.SalePrice * item.Qty}`}</p></strong>
                </div>
            </div>
        </div>
     );
}
 
export default AdditionalItem;
